import { useState,useEffect } from "react";

function AllLeave(){
    const [leaves,setLeaves]=useState([]);
    const [userName,setUserName]=useState("");
    useEffect(()=>{
        let loggedInUser=localStorage.getItem("loggedInUser");
        loggedInUser=loggedInUser ? JSON.parse(loggedInUser) : null;
        setUserName(loggedInUser.email);
    },[])
    const getAllLeaves=()=>{
        fetch(`http://localhost:5000/userleave/allleave`)
        .then(function(res){
            return res.json()
        }).then(function(result){
            console.log("result",result);
            setLeaves(result);
        })
    }
    useEffect(()=>{
        getAllLeaves();
    },[])
    const approveLeave=(e,leave,status)=>{
        e.preventDefault();
        e.preventDefault();
        fetch(`http://localhost:5000/userleave/${leave._id}/approve`,
        {method:"PUT",body:JSON.stringify({status:status}),
        headers:{"Content-Type":"application/json"}})
        .then(function(res){
            return res.json()
        }).then(function(result){
            console.log("Leave Approved");
            getAllLeaves();
        })
    }
    return(
        <table className="table">
            <thead>
                <tr>
                    <th scope="col">UserName</th>
                    <th scope="col">Leave Type</th>
                    <th scope="col">Reason</th>
                    <th scope="col">Status</th>
                    <th scope="col">Start Date</th>
                    <th scope="col">End Date</th>
                    <th scope="col">Action</th>
                </tr>
            </thead>
            <tbody>
                {
                    leaves.map((leave)=>{
                        return(
                            <tr>
                                <td>{leave.userName}</td>
                                <td>{leave.leaveType}</td>
                                <td>{leave.reason}</td>
                                <td>{leave.status}</td>
                                <td>{leave.startDate}</td>
                                <td>{leave.endDate}</td>
                                <td>
                                    <button type="button" class="btn btn-primary" onClick={(e)=>approveLeave(e,leave,"approve")}>Approve</button>
                                    <button type="button" class="btn btn-danger" onClick={(e)=>approveLeave(e,leave,"reject")}>Reject</button>
                                </td>
                            </tr>
                        )
                    })
                }
            </tbody>
        </table>
    )
}

export default AllLeave;
