import { useState,useEffect } from "react";
import AdminHeader from "./AdminHeader";

function AllUsers(){
    const [leaves,setUsers]=useState([]);
    const getAllUsers=()=>{
        fetch(`http://localhost:5000/userleave/allusers`)
        .then(function(res){
            return res.json()
        }).then(function(result){
            console.log("result",result);
            setUsers(result);
        })
    }
    useEffect(()=>{
        getAllUsers();
    },[])
    const approveRejectLeave=(e,leave,status)=>{
        e.preventDefault();
        fetch(`http://localhost:5000/userleave/${leave._id}/approvereject`,
        {method:"PUT",body:JSON.stringify({status:status}),
        headers:{"Content-Type":"application/json"}})
        .then(function(res){
            return res.json()
        }).then(function(result){
            console.log("Leave Approved",result);
            getAllLeaves();
        })
    }
    return(
        <div>
            <AdminHeader/>
            <table className="table">
            <thead>
                <tr>
                    <th scope="col">First Name</th>
                    <th scope="col">Last Name</th>
                    <th scope="col">Email</th>
                    <th scope="col">Role</th>
                    <th scope="col">Leave Count</th>
                </tr>
            </thead>
            <tbody>
                {
                    leaves.map((leave)=>{
                        return(
                            <tr>
                                <td>{leave.firstName}</td>
                                <td>{leave.lastName}</td>
                                <td>{leave.email}</td>
                                <td>{leave.role}</td>
                                <td>{leave.leaveCount}</td>
                            </tr>
                        )
                    })
                }
            </tbody>
        </table>
        </div>
    )
}

export default AllUsers;
